  if service.isEmpty then throw new Exception(s"No service for $lang")
  service.get

def requireNotEmpty(str: String): String =
  if str.isEmpty then throw new Exception("String must not be empty")
  str

def setPreferredLanguage(user: String, lang: String): Unit =
  if user.isEmpty then throw new IllegalArgumentException("User required")
  if lang.isEmpty then throw new Exception("Language required")
  println(s"$user prefers $lang")

def removeUnwantedChars(text: String, chars: Set[Char]): String =
  if chars.isEmpty then throw new Exception("No chars provided to remove")
  text.filterNot(chars.contains)

def answerQuestion(question: String): String =
  if question.endsWith("?") then s"Answering: $question"
  else throw new Exception("Not a valid question")

def parseEnergyValue(str: String): Int =
  if str.forall(_.isDigit) then str.toInt
  else throw new IllegalArgumentException("Invalid energy value")

case class MeterReading(readings: List[Int])
def avgConsumption(meter: MeterReading): Int =
  if meter.readings.isEmpty then throw new Exception("No readings")
  else meter.readings.sum / meter.readings.size

def percentageChange(before: Int, after: Int): Double =
  if before == 0 then throw new ArithmeticException("Cannot divide by zero")
  else ((after - before).toDouble / before) * 100

def getUserDashboard(userId: String): String =
  if userId.isEmpty then throw new IllegalArgumentException("Empty userID")
  else s"Showing dashboard for $userId"

def consumptionPerDay(readings: List[Int]): Int =
  if readings.size < 2 then throw new RuntimeException("Not enough data points")
  else (readings.last - readings.head) / (readings.size - 1)

def accessDevice(devices: List[String], idx: Int): String =
  if idx < 0 || idx >= devices.length then throw new IndexOutOfBoundsException("Invalid index")
  else devices(idx)

def validateThreshold(value: Int, threshold: Int): Boolean =
  if value < 0 then throw new IllegalArgumentException("Negative value")
  else value > threshold

class DashboardConfig(val refreshRate: Int)
def setRefreshRate(cfg: DashboardConfig, rate: Int): Unit =
  if rate <= 0 then throw new IllegalArgumentException("Refresh rate must be positive")
  else cfg.refreshRate = rate

def energyCost(units: Int, costPerUnit: Double): Double =
  if units < 0 then throw new IllegalArgumentException("Negative units")
  else units * costPerUnit

def lastValidReading(readings: List[Int]): Int =
  if readings.isEmpty then throw new Exception("No readings available")
  else readings.last

def devicePower(details: Map[String, Int], device: String): Int =
  if details.contains(device) then details(device)
  else throw new NoSuchElementException(s"Device $device not found")

def parseDate(dateStr: String): java.time.LocalDate =
  try java.time.LocalDate.parse(dateStr)
  catch case _: Exception => throw new IllegalArgumentException("Invalid date format")

def calculateEfficiency(consumed: Int, produced: Int): Double =
  if consumed == 0 then throw new ArithmeticException("Zero consumption")
  else produced.toDouble / consumed

class Meter(val id: String)
def getMeterId(m: Meter | Null): String =
  if m == null then throw new NullPointerException("Meter is null")
  else m.id

def removeOutlier(data: List[Int]): List[Int] =
  if data.size < 3 then throw new Exception("Not enough data to remove outlier")
  else data.sorted.tail.init

def energyLabel(score: Int): String =
  if score < 0 || score > 100 then throw new IllegalArgumentException("Score out of range")
  else if score >= 80 then "A"
  else if score >= 60 then "B"
  else "C"

def safeDivideEnergy(total: Int, hours: Int): Int =
  if hours == 0 then throw new ArithmeticException("Cannot divide by zero hours")
  else total / hours

def getCandidateVotes(votes: Map[String, Int], candidate: String): Int =
  if votes.contains(candidate) then votes(candidate)
  else throw new NoSuchElementException(s"Candidate '$candidate' not found")

def percentageOfTotal(votes: Int, total: Int): Double =
  if total == 0 then throw new ArithmeticException("Total votes cannot be zero")
  else votes.toDouble / total * 100

def parseBallot(ballot: String): (String, Int) =
  val parts = ballot.split(":")
  if parts.length != 2 then throw new IllegalArgumentException("Malformed ballot")
  else (parts(0), parts(1).toInt)

def validateVoteCount(count: Int): Int =
  if count < 0 then throw new IllegalArgumentException("Negative vote count")
  else count

class Tally(var votes: Map[String, Int]):
  def increment(candidate: String): Unit =
    if votes.contains(candidate) then
      votes = votes.updated(candidate, votes(candidate) + 1)
    else throw new NoSuchElementException(s"Candidate $candidate not in list")

def findWinner(votes: Map[String, Int]): String =
  if votes.isEmpty then throw new RuntimeException("No candidates to select winner from")
  else votes.maxBy(_._2)._1

def removeCandidate(votes: Map[String, Int], candidate: String): Map[String, Int] =
  if votes.contains(candidate) then votes - candidate
  else throw new NoSuchElementException(s"Candidate $candidate not present")

def getVotePercentage(candidate: String, votes: Map[String, Int]): Double =
  val total = votes.values.sum
  if !votes.contains(candidate) then throw new NoSuchElementException(s"No such candidate: $candidate")
  if total == 0 then throw new ArithmeticException("Total cannot be zero")
  votes(candidate).toDouble / total * 100

def ballotsCast(voters: Set[String], ballots: Map[String, String]): Int =
  if ballots.keySet != voters then throw new IllegalStateException("Not all voters have cast a ballot")
  else ballots.size

def assignBallot(ballots: Map[Int, String], id: Int, ballot: String): Map[Int, String] =
  if ballots.contains(id) then throw new IllegalArgumentException("Ballot ID already used")
  else ballots + (id -> ballot)

def safeDivideVotes(a: Int, b: Int): Int =
  if b == 0 then throw new ArithmeticException("Cannot divide by zero")
  else a / b

def addCandidate(votes: Map[String, Int], candidate: String): Map[String, Int] =
  if votes.contains(candidate) then throw new IllegalArgumentException("Candidate already exists")
  else votes + (candidate -> 0)

def redistributeVote(votes: Map[String, Int], from: String, to: String, n: Int): Map[String, Int] =
  if !votes.contains(from) || !votes.contains(to) then throw new NoSuchElementException("Candidate missing")
  if votes(from) < n then throw new IllegalArgumentException("Not enough votes to redistribute")
  votes.updated(from, votes(from) - n).updated(to, votes(to) + n)

def parseVoteLine(line: String): (String, Int) =
  val tokens = line.split(",")
  if tokens.size < 2 then throw new IllegalArgumentException("Bad vote line format")
  (tokens(0), tokens(1).toInt)

def requireMinCandidates(votes: Map[String, Int], min: Int): Unit =
  if votes.size < min then
    throw new IllegalArgumentException(s"At least $min candidates required")

def electionQuorum(participants: Int, required: Int): Boolean =
  if required <= 0 then throw new IllegalArgumentException("Required must be positive")
  participants >= required

class VotingRound(maxVotes: Int):
  private var votesCast = 0
  def castVote(): Unit =
    if votesCast >= maxVotes then throw new IllegalStateException("Voting limit reached")
    votesCast += 1

def candidateRank(votes: Map[String, Int], candidate: String): Int =
  if !votes.contains(candidate) then throw new NoSuchElementException("Candidate not found")
  val sorted = votes.toSeq.sortBy(-_._2).map(_._1)
  sorted.indexOf(candidate) + 1

def getISBN(catalog: Map[String, String], title: String): String =
  if catalog.contains(title) then
