  if availableIds.contains(carId) then carId
  else throw new RuntimeException("Requested car not available.")

def calculatePrice(days: Int, dailyRate: Double): Double =
  if days <= 0 then throw new IllegalArgumentException("Invalid number of days.")
  else days * dailyRate

def registerNewCar(fleet: Map[String, Car], newCar: Car): Map[String, Car] =
  if fleet.contains(newCar.plate) then throw new Exception("Car already registered.")
  else fleet + (newCar.plate -> newCar)

class Car(val plate: String, val mileage: Int)
def getMileage(car: Car): Int =
  if car.mileage < 0 then throw new Exception("Mileage cannot be negative.")
  else car.mileage

def assignGps(car: Car, withGps: Boolean): Car =
  if withGps && car.hasGps then throw new Exception("Car already has GPS.")
  else car.copy(hasGps = withGps)

def chargeCustomer(customer: Customer, amount: Double): Unit =
  if amount <= 0 then throw new IllegalArgumentException("Charge amount must be positive.")
  else customer.charge(amount)

def returnCar(fleet: List[Car], car: Car): List[Car] =
  if fleet.exists(_.plate == car.plate) then throw new Exception("Car already returned.")
  else car :: fleet

def upgradeCarPlan(car: Car, newPlan: String): Car =
  val allowedPlans = Set("basic", "premium", "luxury")
  if allowedPlans.contains(newPlan) then car.copy(plan = newPlan)
  else throw new Exception("Invalid car plan.")

def availableCarsCount(cars: List[Car]): Int =
  if cars.isEmpty then throw new Exception("No cars in fleet.")
  else cars.count(_.isAvailable)

def calculateAverageMileage(cars: Seq[Car]): Double =
  if cars.isEmpty then throw new Exception("Fleet is empty.")
  else cars.map(_.mileage).sum.toDouble / cars.length

def updateInsurance(car: Car, active: Boolean): Car =
  if !car.isInsurable then throw new Exception("Car not eligible for insurance.")
  else car.copy(hasInsurance = active)

def setFuelLevel(car: Car, level: Int): Car =
  if level < 0 || level > 100 then throw new Exception("Invalid fuel level.")
  else car.copy(fuelLevel = level)

def calculateRentalShare(totalRentals: Int, companyRentals: Int): Double =
  if totalRentals == 0 then throw new ArithmeticException("Division by zero.")
  else companyRentals.toDouble / totalRentals
