class SleepStats {
  def last7DaysAverage(userSleepRecords: Array[SleepData]): Double = {
    var sum = 0.0
    var count = 0
    for (rec <- userSleepRecords) {
      if (rec != null) {
        sum += rec.hours
        count += 1
      }
    }
    if (count == 0) 0.0 else sum / count
  }
}
