class ChargingNetworkFinder {
  def findStationByIdOrNull(stations: Array[ChargingStation], soughtId: String): ChargingStation = {
    var i = 0
    while (i < stations.length) {
      if (stations(i) != null && stations(i).id == soughtId) {
        return stations(i)
      }
      i += 1
    }
    null
  }
}

