def monthlyPayment(maybePrincipal: Option[Double], rate: Double, months: Int): Double =
  maybePrincipal.map { principal =>
    val monthlyRate = rate / 12 / 100
    val denominator = 1 - math.pow(1 + monthlyRate, -months)
    principal * monthlyRate / denominator
  }.getOrElse(0.0)

