    reservation.cancelled = true
    true
class Reservation(var cancelled: Boolean)

def getLatestDriver(assignment: DriverAssignment): Option[Driver] =
  Option(assignment.driver)
class Driver(val name: String)
class DriverAssignment(val driver: Driver)

def getPromotionDetails(promoCode: String): Promotion =
  if (promoCode == null) null
  else
    new Promotion(promoCode, 10)
class Promotion(val code: String, val discount: Int)

def extractLicensePlate(car: RentalCar): Option[String] =
  Option(car.licensePlate)
class RentalCar(val licensePlate: String)

def findReservationHolder(reservation: Reservation): Staff =
  if (reservation.staff != null) reservation.staff
  else null
class Reservation(val staff: Staff)
class Staff(val name: String)

def getActiveReservationOption(rentals: List[Reservation]): Option[Reservation] =
  rentals.find(_.status == "Active")
class Reservation(val status: String)

def lastServiceDate(vehicle: FleetCar): java.time.LocalDate =
  if (vehicle == null || vehicle.lastService == null) null
  else vehicle.lastService
class FleetCar(val lastService: java.time.LocalDate)

def getBranchLocation(branch: Branch): Option[String] =
  Option(branch.address)
class Branch(val address: String)

def getRentalPrice(rate: RentalRate): java.lang.Double =
  if (rate.price == null) null else rate.price
class RentalRate(val price: java.lang.Double)

def getOptionalLoyaltyId(customer: Customer): Option[String] =
  Option(customer.loyaltyId)
class Customer(val loyaltyId: String)
