case object InvalidBoardingPass extends CheckInError
case object SecurityAlert extends CheckInError

def performCheckIn(boardingPass: Option[String], securityPassed: Boolean): Either[CheckInError, String] = {
  boardingPass match {
    case None => Left(InvalidBoardingPass)
    case Some(_) if !securityPassed => Left(SecurityAlert)
    case Some(bp) => Right(s"Checked in with boarding pass $bp")
  }
}

def calculateFare(base: Double, discount: Option[Double]): Option[Double] = {
  discount match {
    case Some(d) if d >= 0 && d <= 1 => Some(base * (1 - d))
    case None => Some(base)
    case _ => None
  }
}

