sealed trait TransactionError
case class InvalidAmount(amount: BigDecimal) extends TransactionError
case class InsufficientFunds(balance: BigDecimal) extends TransactionError
case class Wallet(walletId: String, balance: BigDecimal)
def sendCrypto(sender: Wallet, recipient: Wallet, amount: BigDecimal): Either[TransactionError, (Wallet, Wallet)] = 
  if amount <= 0 then Left(InvalidAmount(amount))
  else if sender.balance < amount then Left(InsufficientFunds(sender.balance))
  else Right(
    (sender.copy(balance = sender.balance - amount), recipient.copy(balance = recipient.balance + amount))
  )

