class CarNotAvailableException(carId: String) extends Exception(s"Car $carId is not available")

case class Car(id: String, available: Boolean)
def rentCar(carId: String, fleet: List[Car]): Car = {
  val carOpt = fleet.find(_.id == carId)
  if (carOpt.isEmpty) throw new NoSuchElementException(s"Car $carId not found")
  if (!carOpt.get.available) throw new CarNotAvailableException(carId)
  carOpt.get
}

sealed trait ReservationError
case object CarAlreadyReserved extends ReservationError
case object InvalidDateRange extends ReservationError

case class Reservation(carId: String, from: Int, to: Int)
def validateReservation(r: Reservation, reserved: List[Reservation]): Either[ReservationError, Reservation] =
  if (r.to <= r.from) Left(InvalidDateRange)
  else if (reserved.exists(res => res.carId == r.carId && (r.from <= res.to && r.to >= res.from))) Left(CarAlreadyReserved)
  else Right(r)

def removeCarFromFleet(carId: String, fleet: collection.mutable.Buffer[String]): Unit = {
  try {
    if (!fleet.contains(carId)) throw new IllegalArgumentException(s"No car with id $carId.")
    fleet -= carId
  } catch {
    case e: IllegalArgumentException => println("Failed to remove car: " + e.getMessage)
  }
}

class InvalidBookingPeriodException(msg: String) extends Exception(msg)

case class Booking(carId: String, from: Int, to: Int)
def submitBooking(booking: Booking): Booking = {
  if (booking.from >= booking.to)
    throw new InvalidBookingPeriodException("Start date after end date")
  booking
}

case class BookingRequest(customerId: String, carId: String)
sealed trait BookingError
case object CustomerBlocked extends BookingError
case object CarUnderMaintenance extends BookingError

def tryBooking(request: BookingRequest, blockedCustomers: Set[String], underMaintenance: Set[String]): Either[BookingError, String] =
  if (blockedCustomers.contains(request.customerId)) Left(CustomerBlocked)
  else if (underMaintenance.contains(request.carId)) Left(CarUnderMaintenance)
  else Right("Booked")

def calculateInvoice(amount: Double, discount: Option[Double]): Double = {
  try {
    val finalDiscount = discount.getOrElse(0.0)
    if (finalDiscount < 0 || finalDiscount > 1) throw new IllegalArgumentException("Discount out of range")
    amount * (1 - finalDiscount)
  } catch {
    case e: Exception => 0.0
  }
}

import scala.util.Try
case class Car(id: String)
def parseCarId(str: String): Try[Car] = Try {
  if (str.startsWith("CAR-") && str.length > 4) Car(str)
  else throw new Exception("Invalid Car Id")
}

class InvalidCarStatusException(status: String) extends Exception("Invalid status: " + status)

def updateCarStatus(carId: String, status: String): Unit = {
  status match {
    case "available" | "rented" | "maintenance" => ()
    case _ => throw new InvalidCarStatusException(status)
  }
}

sealed trait FleetUpdateError
case class CarDoesNotExist(id: String) extends FleetUpdateError
case object FleetCapacityExceeded extends FleetUpdateError

case class Fleet(max: Int, cars: List[String])
def addCarToFleet(id: String, fleet: Fleet): Either[FleetUpdateError, Fleet] =
  if (fleet.cars.contains(id)) Left(CarDoesNotExist(id))
  else if (fleet.cars.size >= fleet.max) Left(FleetCapacityExceeded)
  else Right(fleet.copy(cars = id :: fleet.cars))

def getRentalPrice(days: Int): Double = {
  try {
    if (days <= 0) throw new IllegalArgumentException("Days must be positive")
    30.0 * days
  } catch {
    case _: IllegalArgumentException => -1.0
  }
}

sealed trait PaymentError
case object InsufficientFunds extends PaymentError
case object CardDeclined extends PaymentError
case object InvalidPaymentDetails extends PaymentError

def attemptPayment(cardNumber: String, amount: Double): Either[PaymentError, String] =
  if (amount > 1000) Left(InsufficientFunds)
  else if (cardNumber.length != 16) Left(InvalidPaymentDetails)
  else Right("Payment Successful")

def extendRentalPeriod(current: Int, extension: Int): Int = {
  try {
    if (extension <= 0) throw new IllegalArgumentException("Extension must be positive")
    current + extension
  } catch {
    case _: IllegalArgumentException => current
  }
}

import scala.util.Try
case class MaintenanceRequest(carId: String, details: String)
def validateMaintenanceRequest(request: MaintenanceRequest): Try[MaintenanceRequest] = Try {
  require(request.details.nonEmpty, "Details required")
  request
}

def cancelBooking(bookingId: String, activeBookings: List[String]): String = {
  try {
    if (!activeBookings.contains(bookingId))
      throw new NoSuchElementException("Booking does not exist")
    "Cancelled"
  } catch {
    case e: NoSuchElementException => e.getMessage
  }
}

case class ReturnCarError(reason: String)
def canReturnCar(isActiveRental: Boolean): Either[ReturnCarError, String] =
  if (isActiveRental) Right("Return processed")
  else Left(ReturnCarError("No active rental for this car"))

def chargeExtra(distanceDriven: Double): Double = {
  try {
    if (distanceDriven < 0) throw new RuntimeException("Negative distance driven")
    if (distanceDriven > 1000)
      throw new IllegalArgumentException("Excessive trip detected")
    0.05 * distanceDriven
  } catch {
    case _: IllegalArgumentException => 100.0
    case _: Throwable => 0.0
  }
}

import scala.util.Try
case class Payment(amount: Double)
def safeCharge(payment: Payment): Try[Double] = Try {
  require(payment.amount > 0, "Amount must be positive")
  payment.amount * 1.1
}

def lookupCustomer(id: String, customers: List[String]): String = {
  try {
    customers.find(_ == id).getOrElse(throw new NoSuchElementException(id))
  } catch {
    case e: NoSuchElementException => s"Customer not found: $id"
  }
}

case class Car(id: String, available: Boolean)
def findAvailableCar(cars: List[Car]): Option[Car] =
  cars.find(_.available)

import scala.util.Try
def parseCarYear(yearString: String): Try[Int] =
  Try(yearString.toInt)

sealed trait RentalError
case object CarNotAvailable extends RentalError
case object CustomerBanned extends RentalError
case class RentalResult(customer: String, car: Car)
def rentCar(car: Car, customerActive: Boolean): Either[RentalError, RentalResult] =
  if (!car.available) Left(CarNotAvailable)
  else if (!customerActive) Left(CustomerBanned)
  else Right(RentalResult("customerId", car.copy(available = false)))

def returnCar(carOpt: Option[Car]): Either[String, Car] =
  carOpt.toRight("Car not found to return")
def markCarReturned(car: Car): Car =
  car.copy(available = true)

case class Reservation(id: String, valid: Boolean)
def cancelReservation(reservation: Reservation): Option[Reservation] =
  if reservation.valid then Some(reservation.copy(valid = false))
  else None

import scala.util.Try
def calculateTotalRentalCost(days: Int, ratePerDay: Double): Try[Double] =
  Try {
    require(days > 0)
    require(ratePerDay >= 0.0)
    days * ratePerDay
  }

def parseLicenseNumber(license: String): Option[String] =
  if license.matches("[A-Z]{2}-\d{6}") then Some(license)
  else None

sealed trait AddCarError
case object DuplicateCar extends AddCarError
def addCarToFleet(fleet: List[Car], car: Car): Either[AddCarError, List[Car]] =
  if fleet.exists(_.id == car.id) then Left(DuplicateCar)
  else Right(car :: fleet)

class CarNotFound(message: String) extends Exception(message)
def fetchCar(carId: String, cars: List[Car]): Car =
  cars.find(_.id == carId).getOrElse(throw new CarNotFound("Car not found"))

def reserveCar(car: Car): Car =
  if car.available then car.copy(available = false)
  else throw new Exception("Car is not available for reservation")

def removeCarFromFleet(fleet: List[Car], targetId: String): List[Car] =
  if fleet.exists(_.id == targetId) then fleet.filterNot(_.id == targetId)
  else throw new Exception("Car to remove does not exist in fleet")

def parseRentalDays(input: String): Int =
  try input.toInt
  catch case _: NumberFormatException => throw new Exception("Invalid rental days input")

def assignCarToCustomer(car: Car, customerId: String): Car =
  if car.available then car.copy(available = false)
  else throw new IllegalStateException("Car is currently unavailable")

def getAvailableCars(cars: List[Car]): List[Car] =
  try cars.filter(_.available)
  catch case e: Exception => throw new RuntimeException("Failed to filter cars", e)

def bookReservation(reservation: Reservation): Reservation =
  if reservation.valid then reservation.copy(valid = false)
  else throw new Exception("Reservation is already invalid")

def processReturn(car: Car, odometer: Int): Car =
  if odometer >= 0 then car.copy(available = true)
  else throw new IllegalArgumentException("Negative odometer reading")

def checkAndGetCar(cars: List[Car], id: String): Car =
  try cars.find(_.id == id).get
  catch case _: NoSuchElementException => throw new Exception("No car found with specified ID")

def updateRentalPeriod(days: Int): Int =
  if days <= 0 then throw new Exception("Rental period must be positive")
  else days
