case class OrderId(value: String)
case class PaymentToken(value: String)
sealed trait CheckoutError
case object InvalidOrder extends CheckoutError
case object PaymentFailed extends CheckoutError

def authorizeOrder(orderId: OrderId): Either[CheckoutError, OrderId] =
  if (orderId.value.nonEmpty) Right(orderId) else Left(InvalidOrder)

def processPayment(token: PaymentToken): Either[CheckoutError, String] =
  if (token.value == "ok") Right("Payment processed") else Left(PaymentFailed)

val result = for {
  authorized <- authorizeOrder(OrderId("123"))
  status     <- processPayment(PaymentToken("ok"))
} yield status

