    if (paymentToken != "valid-token")
      throw new PaymentException("Invalid payment token")
    "Checkout successful"
  }

  def safeProcess(items: List[String], paymentToken: String): String = {
    try {
      process(items, paymentToken)
    } catch {
      case e: Exception => "Checkout error: " + e.getMessage
    }
  }
}

