object CartCheckout {
  def authorizePayment(cardNumber: String, amount: Double): Boolean = {
    if cardNumber.nonEmpty && amount > 0 then true
    else throw new PaymentDeclinedException("Payment was declined.")
  }
  def checkout(items: List[CartItem], cardNumber: String): String = {
    try {
      val total = items.map(_.quantity * 10).sum
      if (authorizePayment(cardNumber, total)) "Checkout completed."
      else throw new Exception("Unexpected failure.")
    } catch {
      case e: PaymentDeclinedException => "Checkout failed: " + e.getMessage
      case e: Exception => "Unknown error: " + e.getMessage
    }
  }
}

