def processCheckout(cartItems: List[String], shippingAddress: String, paymentToken: String): Either[CheckoutError, CheckoutSummary] = {
  if cartItems.isEmpty then Left(EmptyCartError)
  else if shippingAddress.isBlank then Left(InvalidShippingInfo)
  else if paymentToken.length < 12 then Left(PaymentError("Payment token invalid."))
  else Right(CheckoutSummary("ORD-98214", cartItems.length * 55.0))
}

