def averageCO2Levels(values: Seq[String]): Double =
  var total = 0.0
  var n = 0
  for (v <- values) {
    try {
      total += v.toDouble
      n += 1
    } catch {
      case _: NumberFormatException => // skip invalid
    }
  }
  if (n == 0) throw new Exception("No valid CO2 values")
  total / n

