case object NoSuchStop extends JourneyError
case object NoConnection extends JourneyError
case class TimetableConflict(details: String) extends JourneyError
def findConnection(source: String, target: String, graph: Map[String, List[String]]): Either[JourneyError, List[String]] =
  if !graph.contains(source) then Left(NoSuchStop)
  else if source == target then Right(List(source))
  else graph(source).find(_ == target) match
    case Some(_) => Right(List(source, target))
    case None => Left(NoConnection)

