import scala.util.Try

def safeMonthlyPayment(principal: Double, annualRate: Double, termMonths: Int): Try[Double] =
  Try {
    val monthlyRate = annualRate / 12.0 / 100.0
    val payment = principal * monthlyRate / (1 - math.pow(1 + monthlyRate, -termMonths))
    if payment.isNaN || payment.isInfinity then throw new Exception("Computation failed.")
    payment
  }

