import scala.util.Try

def safeMonthlyPayment(loan: Double, rate: Double, years: Int): Try[Double] = Try {
  val monthlyInterestRate = rate / 12 / 100
  val n = years * 12
  val denominator = 1 - Math.pow(1 + monthlyInterestRate, -n)
  if (denominator == 0) throw new ArithmeticException("Zero denominator")
  loan * monthlyInterestRate / denominator
}

def logAndReturnPayment(principal: Double, rate: Double, term: Int): Double = {
  try {
    val monthlyRate = rate / 12 / 100
    val payments = term * 12
    principal * monthlyRate / (1 - Math.pow(1 + monthlyRate, -payments))
  } catch {
    case ex: Throwable =>
      println("Error calculating payment: " + ex.getMessage)
      throw ex
  }
}

