def checkForOverlappingEntries(entries: List[(Long, Long)]): Either[TimeTrackingIssue, Unit] =
  val sorted = entries.sortBy(_._1)
  val overlaps = sorted.sliding(2).exists {
    case List((_, end1), (start2, _)) => end1 > start2
    case _ => false
  }
  if overlaps then Left(TimeTrackingIssue("Overlapping entries detected")) else Right(())

