def calculatePathLength(points: List[(Double, Double)]): Double = {
  if (points.length < 2) throw new PathOptimizationException("At least two points needed")
  points.sliding(2).map {
    case List((x1, y1), (x2, y2)) => math.hypot(x2 - x1, y2 - y1)
    case _ => 0.0
  }.sum
}

