case class OptimizedPath(waypoints: List[(Double, Double)])
def computeOptimizedPath(battery: Double, candidates: List[List[(Double, Double)]]): Either[PathOptimizationError, OptimizedPath] = {
  if (battery < 20.0) Left(InsufficientBattery)
  else candidates.headOption match {
    case Some(path) => Right(OptimizedPath(path))
    case None => Left(NoAvailableRoute)
  }
}

