sealed trait PathPlanningError
case class ObstacleDetected(coords: (Double, Double)) extends PathPlanningError
case object MaxRangeExceeded extends PathPlanningError

def planPathWithObstacleAvoidance(start: (Double, Double), end: (Double, Double), obstacles: List[(Double, Double)], maxRange: Double): Either[PathPlanningError, List[(Double, Double)]] =
  if (obstacles.exists(obs => obs == start || obs == end)) Left(ObstacleDetected(start))
  else if (Math.abs(start._1 - end._1) + Math.abs(start._2 - end._2) > maxRange) Left(MaxRangeExceeded)
  else Right(List(start, end))

