def recalculatePathLength(path: List[(Double, Double)]): Double =
  if (path.size < 2) throw new IllegalArgumentException("Path too short to calculate length.")
  (path zip path.tail).map { case (a, b) =>
    val dx = a._1 - b._1
    val dy = a._2 - b._2
    Math.sqrt(dx*dx + dy*dy)
  }.sum

